```C++
assert(real->GetMainMemoryUsage() == real->GetMainMemoryUsage());

if (real->GetTransactionCount(TxGraph::Level::MAIN) == 0) {
    assert(real->GetMainMemoryUsage() == 0);
} else {
    assert(real->GetMainMemoryUsage() > 0);
}

