bool CWallet::IsFromMe(const CTransaction& tx) const
{
    return std::any_of(tx.vin.begin(), tx.vin.end(),
        [this](const CTxIn& txin) { return WITH_LOCK(cs_wallet, return GetTXO(txin.prevout)); });
}
