   base58.cpp
   bech32.cpp
   bip324_ecdh.cpp
+  arith_uint256.cpp
   block_assemble.cpp
   blockencodings.cpp
   ccoins_caching.cpp
diff --git a/src/bench/arith_uint256.cpp b/src/bench/arith_uint256.cpp
new file mode 100644
--- /dev/null	(date 1757366730590)
+++ b/src/bench/arith_uint256.cpp	(date 1757366730590)
@@ -0,0 +1,53 @@
+// src/bench/arith_uint256.cpp
+#include <bench/bench.h>
+#include <arith_uint256.h>
+#include <random.h>
+#include <uint256.h>
+#include <vector>
+
+static void ArithUint256CopyConstruct(benchmark::Bench& bench)
+{
+    FastRandomContext rng{/*fDeterministic=*/true};
+    const arith_uint256 source{UintToArith256(rng.rand256())};
+
+    bench.run([&] {
+        arith_uint256 dest{source};
+        ankerl::nanobench::doNotOptimizeAway(dest);
+    });
+}
+
+
+static void ArithUint256CopyAssign(benchmark::Bench& bench)
+{
+    FastRandomContext rng{/*fDeterministic=*/true};
+    const arith_uint256 source{UintToArith256(rng.rand256())};
+    arith_uint256 dest{UintToArith256(rng.rand256())};
+
+    bench.run([&] {
+        dest = source;
+        ankerl::nanobench::doNotOptimizeAway(dest);
+    });
+}
+
+static void ArithUint256MoveCircle(benchmark::Bench& bench)
+{
+    FastRandomContext rng{
+        /*fDeterministic=*/true};
+    arith_uint256 a{UintToArith256(rng.rand256())};
+    arith_uint256 b{UintToArith256(rng.rand256())};
+    arith_uint256 c{UintToArith256(rng.rand256())};
+
+    bench.run([&] {
+        arith_uint256 temp{std::move(a)};
+        a = std::move(b);
+        b = std::move(c);
+        c = std::move(temp);
+        ankerl::nanobench::doNotOptimizeAway(a);
+        ankerl::nanobench::doNotOptimizeAway(b);
+        ankerl::nanobench::doNotOptimizeAway(c);
+    });
+}
+
+BENCHMARK(ArithUint256CopyConstruct, benchmark::PriorityLevel::HIGH);
+BENCHMARK(ArithUint256CopyAssign, benchmark::PriorityLevel::HIGH);
+BENCHMARK(ArithUint256MoveCircle, benchmark::PriorityLevel::HIGH);

```

</details>

