        if (pfrom.IsPrivateBroadcastConn()) {
            const auto pushed_tx_opt{!m_tx_for_private_broadcast.has_value() ? std::nullopt : m_tx_for_private_broadcast->GetTxForNode(pfrom.GetId())};
            if (!pushed_tx_opt) {
                LogInfo( ...
                ...
            }
            ...
        }
