 map<string, vector<string> > _mapMultiArgs;
+static std::map<std::string, std::vector<std::string> > mapMultiArgs_;
+const std::map<std::string, std::vector<std::string> >& mapMultiArgs = mapMultiArgs;
 bool fDebug = false;

